import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import '../css/App.css';
import Text from './projectPage/Text.js'
import ScrollAnimation from 'react-animate-on-scroll';

function ArticleThumbnail(props) {
  return (
    <Link to={props.link}>
      <div className="divider">
        <hr className="solid"/>
      </div>
      <ScrollAnimation animateIn="fadeIn">
        <div className="container-fluid p-md-0 ">
          <div className="row sectionOne justify-content-center">
            <div className="sectionOne-img set-bg col-lg-4" style={{backgroundImage: "url(" + props.image + ")"}}></div>
            <div className='col-lg-6'>
              <h1 className="sectionOne-title">{props.title}</h1>
              <p className="sectionOne-text">{props.text}</p>
            </div>
          </div>
        </div>
      </ScrollAnimation>
    </Link>
  );
}

export default ArticleThumbnail;
