import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class SectionOne extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title:props.title,
      text:props.text,
      image:props.image,
      height:props.height
    }
  }

  render() {
    return(
      <div className="container-fluid p-md-0 ">
        <div className="row sectionOne">
          <div className='col-lg-6'>
            <h1 className="sectionOne-title">{this.state.title}</h1>
            <p className="sectionOne-text">{this.state.text}</p>
          </div>
          <div className="col-lg-6 sectionOne-img justify-content-center">
            <img src={this.state.image} style={{height:this.state.height}}/>
          </div>
        </div>
      </div>
    )
  }
}

export default SectionOne;
