import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Typing from 'react-typing-animation';


function Intro(props) {

  const [introVis, changeIntroVis] = useState(true)

  return (
    <div className={`${introVis ? "intro-wrapper":"intro-invis"}`} onFinishedTyping={()=>{changeIntroVis(false)}}>
      <Typing>
        <a>Hi, I'm William, a design engineer based in London.</a>
      </Typing>
    </div>
  );
}

export default Intro;
