import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class Text extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      col:props.col,
      height:props.height,
      text:props.text,
      align:props.align,
      font:props.font,
      indent:props.indent
    }
  }

  render() {
    return(
      <div className={this.state.col+" text-wrapper"}>
        <div className="" style={{textAlign:this.state.align,height:this.state.height,fontSize:this.state.font}}><Markdown source={this.state.text} style={{marginLeft:this.state.indent}}/></div>
      </div>
    )
  }
}

export default Text;
