import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'
import Text from './../projectPage/Text.js'

import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/reveng/thumbnail.jpg'),
}

const a = {
  'title':'Product Teardown',
  'text':'The product was stripped down as far as possible. All the components were listed and each material identified. After this the components were placed on a teardown display board in order to illustrate how the product was constructed. The process was to determine how best to reduce the energy consumption of the product at the design stage. Factors considered of the embodied energy of the materials, the manufacturing processes used and the usage of the product by consumers. Making these decisions at the design stage hugely reduces cost, as once manufacturing has begun it is very expensive to make alterations to the product, due to the cost of metal and plastic moulds. ',
  'image':require('./../../content/projects/reveng/1.jpg'),
}




function Reveng(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='reveng'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <Text
         text='**Background**'
         col="col-12"
         height="10px"
         align="left"
         font="100px"
         type="title-wrapper"
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
      </div>
    )
}

export default Reveng;
