import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'
import Text from './../projectPage/Text.js'


import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/leapfrog/thumbnail.png'),
}

const a = {
  'title':'Preliminary Analysis',
  'text':'An optimum takeoff angle of 76 degrees calculated as the x and y distance are equal (no tradeoff between horizontal and vertical distance).',
  'image':require('./../../content/projects/leapfrog/1.png'),
}

const b = {
  'title':'Tranmission',
  'text':'A gear ratio of 48 was determined to be suitable. Using spur gears the maximum ratio between two gears is 5. To reduce the mechanism size as much as possible without compromising torque and efficiency too much, a module of 0.5 was determined. The minimum number of teeth for a spur gear is 12 and so the gear ratio in terms of tooth size was determined as such: 12/50, 12/34, 14/57. This would step the motor rpm down from 19000 to 395 (a step down of factor 48).',
  'image':require('./../../content/projects/leapfrog/2.png'),
}

const c = {
  'title':'Structural Integrity',
  'text':'The head plate has to withstand the force of the spring when it is fully compressed and so a static analysis was performed on it, modelling the force on the bottom of the head plate. The safety factor was determined to be 0.45 and so would not withstand the force. Supports were added to the bottom of the head plate to give it extra rigidity, especially around the spring support where the spring has an interference fit. This increased the safety factor to 1.85',
  'image':require('./../../content/projects/leapfrog/3.png'),
}

const d = {
  'image':require('./../../content/projects/leapfrog/4.png'),
}


function LeapFrog(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='leapfrog'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <Text
         text='Engineering Analysis'
         col="col-12"
         align="left"
         type="title"
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <SectionOne
         title={b.title}
         text={b.text}
         image={b.image}
        />
        <SectionOne
         title={c.title}
         text={c.text}
         image={c.image}
        />
        <Divider/>
        <Text
         text='Technical Drawing'
         col="col-12"
         type="title"
        />
        <Image
         image={d.image}
        />
      </div>
    )
}

export default LeapFrog;
