import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'
import ArticleDataLoader from './articleData.js';

class ArticleHeader extends React.Component {
  constructor(props) {
    super(props);

    const articleData = ArticleDataLoader();
    //console.log(projectData);

    for (var i =0; i<articleData.length; i++){
      if (articleData[i].key === props.articleKey){
        var article = articleData[i]
        //console.log(project)
        break
      }
    }

    this.state = {
      article:article,
    }
  }

  render() {
    return(
      <div className='projectHeader'>
        <div className='row projectHeader-text'>
          <div className='col-lg-12'>
            <h1>{this.state.article.title}</h1>
            <h2><Markdown source={this.state.article.description}/></h2>
          </div>
        </div>
      </div>
    )
  }
}

export default ArticleHeader;
