import React, { useState, useEffect, useRef } from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import {CSSTransition,TransitionGroup} from 'react-transition-group';
import ProjectDataLoader from './projectData.js';

function Projects(props) {
  const projects = ProjectDataLoader()

  const [state, setState] = useState({
    activeCategory:props.location.activeCategory,
    filterProjects:projects.sort((a,b) => parseInt(b.year)-parseInt(a.year)),
  });

  useEffect(() => {
    setState({
      filterProjects:(state.category==='all' || state.category===null) ? projects : projects.filter(project => project.category.includes(state.category))
    });
  },[state]);

  return (
      <section className="portfolio-section">
        <div className="portfolio-section-background set-bg" style={{backgroundImage:"url(" + require("./../content/background.png") + ")"}}/>
    		<div className="container">
    			<ul className="portfolio-filter">
    				<li className={`strikeNav ${(state.activeCategory==='all') ? "filter-active" : "filter-inactive"}`} onClick={() => setState({activeCategory:'all'})}>All</li>
    				<li className={`strikeNav ${(state.activeCategory==='cod') ? "filter-active" : "filter-inactive"}`} onClick={() => setState({activeCategory:'cod'})}>Coding and Electronics</li>
    				<li className={`strikeNav ${(state.activeCategory==='des') ? "filter-active" : "filter-inactive"}`} onClick={() => setState({activeCategory:'des'})}>Design</li>
    				<li className={`strikeNav ${(state.activeCategory==='eng') ? "filter-active" : "filter-inactive"}`} onClick={() => setState({activeCategory:'eng'})}>Engineering Analysis</li>
    				<li className={`strikeNav ${(state.activeCategory==='str') ? "filter-active" : "filter-inactive"}`} onClick={() => setState({activeCategory:'str'})}>Enterprise and Systems</li>
    			</ul>
    		</div>
        <TransitionGroup>
          <CSSTransition
                classNames="portfolioFade"
                timeout={800}
                key={0}
            >
        		<div className="container-fluid p-md-0 ">
        			<div className="row portfolio-area" >
                {state.filterProjects.map((thumbnail) => (
                  <Thumbnail
                    key={thumbnail.title}
                    link={thumbnail.link}
                    image={thumbnail.image}
                    title={thumbnail.title}
                    question={thumbnail.question}
                    col={thumbnail.col}
                    />
                ))}
        			</div>
        		</div>
          </CSSTransition>
        </TransitionGroup>
    	</section>
  )
}

export default Projects;
