import React, { Component } from 'react'; // Import the Component component from React

import { NavLink, Link } from 'react-router-dom';
import SocialBar from './SocialBar.js';

import styled from "styled-components";


const Navigation = styled.header`
  position: relative;
  min-width:100%;
  transition: all .2s ease-in-out;
  z-index: 1;
  display: flex;
  justify-content: space-between;
  align-items: center;
  align-content: center;
  height: 140px;


  &:hover{
    background:white;
  }

  a {
    color: #979797;
    transition: all 0.6s;
    font-size: 1em;
  }

  a:hover {
    color:#4A03FF;
  }

  .fa-bars {
    display: none;
    color: #979797;
    font-size: 2rem;
  }

  .fa-bars:hover {
    color:#4A03FF;
  }

  .logo-wrapper{
    position:fixed
    width:100%;
    text-align:center;
  }

  .site-logo {
    font-size: 24px;
    padding-top: 20px;
    font-weight: 200;
  }

  nav {
    ul {
      display: flex;
      justify-content: space-between;
    }
    li {
      margin: 0 15px;
      justify-content: space-between;
      font-size: 1em;
    }

    li{
      color:#4A03FF;
    }


    a{
      font-size: 1em;
      text-decoration: none;
      .active {
        color: #979797;
      }
    }

    .activePage{
      color:#4A03FF;
    }

    a.active {
      color: #4A03FF;
    }
  }

  .social-links {
    margin-top: 10px;
    margin-bottom: 40px;
  }

  .social-links a:hover{
    color: #4A03FF;
  }

  .social-links a {
    color: #979797;
    font-size: 30px;
    margin-right: 30px;
  }

  .social-links a:last-child {
    margin-right: 0;
  }

  @media only screen and (max-width: 1250px) {
    height: auto;
    min-height: 50px;
    display: block;
    position: relative;

    .fa-bars {
      display: inline-block;
      position: absolute;
      top: 10px;
      right: 10px;
      cursor: pointer;
    }

    .site-logo {
      font-size: 18px;
      text-align: center;
      position: absolute;
      top: 0px;
    }

    div.collapsed {
      width: 100%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      flex-wrap: wrap;

      overflow: hidden;
      max-height: 0;
      -moz-transition-duration: 0.4s;
      -webkit-transition-duration: 0.4s;
      -o-transition-duration: 0.4s;
      transition-duration: 0.4s;
      -moz-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      -webkit-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      -o-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      transition-timing-function: cubic-bezier(0, 1, 0.5, 1);

      &.is-expanded {
        overflow: hidden;
        max-height: 500px; /* approximate max height */
        -moz-transition-duration: 0.4s;
        -webkit-transition-duration: 0.4s;
        -o-transition-duration: 0.4s;
        transition-duration: 0.4s;
        -moz-transition-timing-function: ease-in;
        -webkit-transition-timing-function: ease-in;
        -o-transition-timing-function: ease-in;
        transition-timing-function: ease-in;
      }
      li {
        padding: 15px 10px;
        margin: 0px 0px;
        width: 100%;
      }
    }
  }
`;


class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isExpanded: false
    };
  }
  handleToggle(e) {
    e.preventDefault();
    this.setState({
      isExpanded: !this.state.isExpanded
    });
  }
  render() {
    const { isExpanded } = this.state;

    return (
      <Navigation>
        <div className="logo-wrapper container">
          <h2><Link to="/" className="site-logo">William Pepera</Link></h2>
        </div>

      </Navigation>
    );
  }
}

export default Nav;
