import React, {useState} from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import ProjectDataLoader from './projectData.js';

function HomepageProject(props) {
  const projectData = ProjectDataLoader();
  //console.log(projectData);

  const [visible, setVisible] = useState(false);

  for (var i =0; i<projectData.length; i++){
    if (projectData[i].key === props.projectKey){
      var project = projectData[i]
      //console.log(project)
      break
    }
  }

  return (
    <div className="no-gutters" style={{width:"100%"}}>
      <Link to={project.link}>
        <div className="homepageProject col-12 no-gutters"
          onMouseOver={e => (e.currentTarget.style.background = props.colour)}
          onMouseOut={e => (e.currentTarget.style.background = "white")}
        >
          <div className="homepageProject-img set-bg slide-left" style={{backgroundImage: "url(" + project.image + ")"}}/>
          <div className="hp-inner">
            <h3 className="project-number">{props.number+" / "+props.total}</h3>
            <h1 className=""
              onMouseOver={() => setVisible(true)}
              >{" "+project.title}</h1>
            <h2 className={"project-question "+visible ? "" : "invisible"}>{project.question}</h2>
            <h4 className="fa fa-long-arrow-right strike"> see project</h4>
          </div>
        </div>
      </Link>
    </div>
  );
}

export default HomepageProject;
