import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class Text extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      col:props.col,
      text:props.text,
      align:props.align,
      font:props.font,
      type:props.type,
    }
  }

  render() {
    return(
      <div className={this.state.col}>
        <div className={this.state.type} style={{textAlign:this.state.align,height:this.state.height,fontSize:this.state.font}}>{this.state.text}</div>
      </div>
    )
  }
}

export default Text;
