import React from 'react'; // Import the Component component from React
import ProjectCarousel from './../projectPage/projectCarousel.js'
import ProjectHeader from './../projectPage/ProjectHeader.js'
import SlideImage from './../projectPage/slideImage.js'


function Panaseer(props) {
  const slides = [
    <SlideImage
      image={require('./../../content/projects/panaseer/hero-image.png')}
    />,
    <SlideImage
      image={require('./../../content/projects/panaseer/1.png')}
    />,
    <SlideImage
      image={require('./../../content/projects/panaseer/23.jpg')}
    />,
    <SlideImage
      image={require('./../../content/projects/panaseer/5.png')}
    />,
    <SlideImage
      image={require('./../../content/projects/panaseer/15.png')}
    />
  ]

  const slideInfo = [
    [
      <ProjectHeader
        projectKey={"panaseer"}
      />
    ],
    [
      <h2>Key Features</h2>,
      <h3>slide text</h3>,
    ],
    [
      <h2>IR Spectroscopy</h2>,
      <h3>The core technology behind Panaseer is Infrared spectroscopy which allows for the identification of organic compunds by projecting a range of infrared frequencies onto the compund and measuring which frequencies are returned, as different types of chemical bond absorb different frequencies. Taking the types and numbers of bonds into account, specific compounds can be identified by comparison to a database.</h3>,
    ],
    [
      <h1>User Experience</h1>,
      <h2>Passive Scanning</h2>,
      <h3>Panaseer is designed to be primarily worn on the wrist of the user’s dominant hand with the module on the underside of the wrist. This is to allow for passive scanning of everything the user picks up. A short wave infrared semi-conductor sensor uses a thin-film encapsulation technique to allow IR spectroscopy at a small scale, a technology developed by Hertzstück. Whilst Panaseer can also be used as a handheld device, active scanning, particularly with younger users, may, at a point, feel tedious. Some level of protection is therefore added by the passive scanning and active scanning can be used where there is a greater need for scanning or an indication of danger.</h3>,
    ],
    [
      <h1>slide 4</h1>,
    ]
  ]

  return(
    <div>
      <ProjectCarousel
        slides={slides}
        slideInfo={slideInfo}
      />
    </div>
  )
}

export default Panaseer;
