import React, { useState, useEffect, useRef } from 'react';
import { Lightbox } from "react-modal-image";

function SlideImage(props) {
  const mobile = useWindowWidth() <= 991;

  const [lightbox, toggleLightbox] = useState(false)

  const slide = useRef()

  useEffect(() => {
    // add when mounted
    document.addEventListener("click", handleClick);
    // return function to be called when unmounted
    return () => {
      document.removeEventListener("click", handleClick);
    };
  }, [lightbox]);

  const handleClick = e => {
    if (slide.current.contains(e.target)) {
      toggleLightbox(true);
      return;
    }
  };

  return(
    <div>
      <Lightbox
        alt=""
        medium={props.image}
        large={props.image}
      />
      <div className="slide" ref={slide} style={{backgroundImage:"url("+(mobile ? "" : props.image)+")"}}>
        <img src={mobile ? props.image : ""} style={{display:(mobile ? "" : "none")}} alt="default"/>
      </div>
    </div>
  )
}

const useWindowWidth = () => {
  const [windowWidth, setWindowWidth ] = useState(window.innerWidth);

  useEffect(() => {
      const handleWindowResize = () => {
          setWindowWidth(window.innerWidth);
      };

      window.addEventListener('resize', handleWindowResize);
      return () => window.removeEventListener('resize', handleWindowResize);
  },[windowWidth]);

  return windowWidth;
};

export default SlideImage;
